(()=>{const s=document.querySelector("#button-open-menu"),e=document.querySelector("#button-close-menu"),o=document.querySelector(".navlist"),t=document.body;s.addEventListener("click",(()=>{o.classList.contains("off")&&(o.classList.remove("off"),s.classList.add("off"),e.classList.remove("off"),t.classList.add("no-scroll"))})),e.addEventListener("click",(()=>{o.classList.add("off"),s.classList.remove("off"),e.classList.add("off"),t.classList.remove("no-scroll")}))})();